<link rel="stylesheet" href="http://localhost/ejaidaad/assets/star/css/main.mindf0b.css">
<script type='text/javascript' src='http://justcapelle.com/slick-modal-css3-jquery-popup/js/lib/jquery.js'></script>

<div class="demo">
    <div class="demoLabel mainDemo-17">Profile settings</div>
</div>

<div class="userProfile" data-sm-init="true">
    <div class="top">
        <div class="left">
            <img class="pic" src="#" />
        </div>
        <div class="right">
            <div class="name">John Doe</div>
            <div class="desc">Lorem ipsum dot color due vent si amet.</div>
            <a href="#">Edit username</a>
            <a href="#">Change password</a>
        </div>
    </div>
    <form id="profileSettings">
        <div class="title">My preferences</div>
        <div class="field">
            <div class="subtitle">Hide my profile from search</div>
            <div class="chk">
                <input type="checkbox" name="option-1" id="option-1" checked />
                <label for="option-1" data-off="off" data-on="on"></label>
            </div>
        </div>
        <div class="field">
            <div class="subtitle">Enable PM messaging</div>
            <div class="chk">
                <input type="checkbox" name="option-2" id="option-2" />
                <label for="option-2" data-off="off" data-on="on"></label>
            </div>
        </div>
        <div class="field">
            <div class="subtitle">Share your info with others</div>
            <div class="chk">
                <input type="checkbox" name="option-3" id="option-3" />
                <label for="option-3" data-off="off" data-on="on"></label>
            </div>
        </div>
        <div class="field">
            <div class="subtitle">Subscribe to newsletter</div>
            <div class="chk">
                <input type="checkbox" name="option-4" id="option-4" />
                <label for="option-4" data-off="off" data-on="on"></label>
            </div>
        </div>
    </form>

</div>

